import React, { useEffect, useState } from 'react'
import Form from 'react-bootstrap/Form';
import './Style.css'


function Home() {

    const [inputSelector,setInputSelector] = useState('');
    const [wthrArray,setWthrArray] = useState([]);


    useEffect(() => {
    if(inputSelector!==''){
     runFetch()
    }

    }, [inputSelector])
    
   const selected=(e)=>{
      setInputSelector(e.target.value)
   }


   const runFetch= async()=>{
    try {
        const data = await fetch(`https://api.weatherapi.com/v1/forecast.json?key=3def0588de354cfcbd4233340243011&q=${inputSelector}`,{
            method:'GET',
            headers:{
                'Content-Type':'application/json'
            },
            
        })
        const result = await data.json()
        console.log(result.forecast.forecastday.hour) 
        setWthrArray( result.current.condition.icon)
    } catch (error) {
        
    }
   }

  return (
    <>
    <div className='mid'>
     <select value={inputSelector} onChange={selected}>
        <option value=''>Select Cities</option>
        <option value='Mumbai'>Mumbai</option>
        <option value='Aizwal'>Aizwal</option>
        <option value='Nagpur'>Nagpur</option>
        <option value='Pune'>Pune</option>
     </select>

        <div className='label'>
            <div>
                <img src={wthrArray} />
            </div>
        </div>
    </div>
    </>
  )
}

export default Home
